input EdgeServerInfo {
  bootId: String!
  executableSchemaId: String!
  graphVariant: String! = "current"
  libraryVersion: String
  platform: String
  runtimeVersion: String
  serverId: String
  userVersion: String
}

interface ReportServerInfoResult {
  inSeconds: Int!
  withExecutableSchema: Boolean!
}

type ReportServerInfoResponse implements ReportServerInfoResult {
  inSeconds: Int!
  withExecutableSchema: Boolean!
}

type ReportServerInfoError implements ReportServerInfoResult {
  code: ReportServerInfoErrorCode!
  inSeconds: Int!
  message: String!
  withExecutableSchema: Boolean!
}

type Mutation {
  service(id: ID!): ServiceMutation!
}

type ServiceMutation {
  reportServerInfo(
    "Only sent if previously requested i.e. received ReportServerInfoResponse with withExecutableSchema = true"
    executableSchema: String,
    info: EdgeServerInfo!
  ): ReportServerInfoResult
}

graph TB;
  activeSchema(Does the specified variant already have an active schema?);
  exactReported("Does the active schema match any schema reported in the last 90 seconds?");
  registerRecent(Register the schema most recently received by the registry);
  keepActive(Keep the current active schema);
  activeSchema --"No"-->registerRecent;
  activeSchema --"Yes"-->exactReported;
  exactReported--"No"-->registerRecent;
  exactReported--"Yes"-->keepActive;
  class registerRecent,keepActive secondary;
